 defined('LIBS_PATH') or die; abstract class JDatabase { public function query() { return $this->execute(); } public static function getConnectors() { return JDatabaseDriver::getConnectors(); } public function getErrorMsg($escaped = false) { if ($escaped) { return addslashes($this->errorMsg); } else { return $this->errorMsg; } } public function getErrorNum() { return $this->errorNum; } public static function getInstance($options = array()) { return JDatabaseDriver::getInstance($options); } public static function splitSql($query) { return JDatabaseDriver::splitSql($query); } }